package JavaBasicConcepts;

public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 50;
		int b = 40;

		a = a + b;
		System.out.println("Value of a is: " + a);

		b = a - b;
		System.out.println("Value of b is: " + b);

		a = a - b;
		System.out.println("Value of a is: " + a);

	}

}
