package JavaBasicConcepts;

public class IfConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 50;
		int b = 60;
		
		if(a > b) {
			System.out.println("I am in If");
		}
		System.out.println("I am not in If");
		
		System.out.println("********************************************************");
		
		if(a < b) {
			System.out.println("I am in If");
		}
		System.out.println("I am not in If");
	}

}
