package JavaBasicConcepts;

public class IfElseConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 50;
		int b = 60;
		
		if(a > b) {
			System.out.println("I am in If");
		}else {
			System.out.println("I am in Else");
		}
		
		System.out.println("****************************************************************");
		
		if(a < b) {
			System.out.println("I am in If");
		}else {
			System.out.println("I am in Else");
		}
	}

}
